<script>
// 全局变量
let timerInterval;
let seconds = 0;
const audio = new Audio('https://assets.mixkit.co/active_storage/sfx/2568/2568-preview.mp3');

// 生成唯一的实例ID
const instanceId = Date.now().toString();

function playSound() {
    audio.currentTime = 0;
    audio.play();
}

function updateTimer() {
    const timerElement = document.getElementById('timer');
    seconds++;
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    timerElement.textContent = `${minutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;

    // 只有在通话未结束时才保存时间
    if (!localStorage.getItem('callEnded')) {
        localStorage.setItem('lastTimerValue', seconds.toString());
    }
}

function startTimer() {
    // 检查通话是否已结束
    if (!localStorage.getItem('callEnded')) {
        // 检查是否有之前保存的时间
        const savedSeconds = localStorage.getItem('lastTimerValue');
        if (savedSeconds) {
            seconds = parseInt(savedSeconds);
            // 立即更新显示
            const minutes = Math.floor(seconds / 60);
            const remainingSeconds = seconds % 60;
            document.getElementById('timer').textContent =
                `${minutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
        }
    } else {
        // 如果通话已结束，重置计时器
        seconds = 0;
        document.getElementById('timer').textContent = '00:00';
        localStorage.removeItem('callEnded');
        localStorage.removeItem('lastTimerValue');
    }

    // 检查是否有其他活动的计时器实例
    const activeInstance = localStorage.getItem('activeTimerInstance');
    if (activeInstance && activeInstance !== instanceId) {
        // 如果有其他活动实例，暂停它
        localStorage.setItem('timerPaused_' + activeInstance, 'true');
    }

    // 设置当前实例为活动实例
    localStorage.setItem('activeTimerInstance', instanceId);

    // 开始新的计时器
    if (!timerInterval) {
        timerInterval = setInterval(updateTimer, 1000);
    }
}

function pauseTimer() {
    clearInterval(timerInterval);
    timerInterval = null;
    localStorage.setItem('timerPaused_' + instanceId, 'true');
}

function endCall() {
    playSound();
    pauseTimer();
    const currentTime = document.getElementById('timer').textContent;

    // 标记通话已结束
    localStorage.setItem('callEnded', 'true');

    // 发送消息到聊天系统
    if (typeof triggerSlash === 'function') {
        triggerSlash(`/send 挂断语音通话，时长${currentTime}|/trigger`);
    } else {
        console.error('triggerSlash 函数不可用');
    }
}

// 添加闪光效果
function addSparkles() {
    const decoration = document.querySelector('.decoration');
    for(let i = 0; i < 10; i++) {
        const sparkle = document.createElement('div');
        sparkle.className = 'sparkle';
        sparkle.style.left = Math.random() * 100 + '%';
        sparkle.style.top = Math.random() * 100 + '%';
        sparkle.style.animationDelay = Math.random() * 2 + 's';
        decoration.appendChild(sparkle);
    }
}

// 检查计时器是否应该暂停
function checkTimerPause() {
    if (localStorage.getItem('timerPaused_' + instanceId) === 'true') {
        pauseTimer();
    }
}

// 页面加载时的初始化
document.addEventListener('DOMContentLoaded', () => {
    startTimer();
    addSparkles();

    // 定期检查是否应该暂停
    setInterval(checkTimerPause, 1000);
});

// 页面关闭或刷新时的清理
window.addEventListener('beforeunload', () => {
    if (instanceId === localStorage.getItem('activeTimerInstance')) {
        localStorage.removeItem('activeTimerInstance');
    }
});

// 添加新的JavaScript函数
function showVoiceModal() {
    document.getElementById('voiceModal').style.display = 'block';
}

function cancelVoice() {
    document.getElementById('voiceModal').style.display = 'none';
}

function confirmVoice() {
    const voiceInput = document.getElementById('voiceInput').value;
    if (voiceInput.trim() !== '') {
        if (typeof triggerSlash === 'function') {
            triggerSlash(`/send 回复语音通话和$1：${voiceInput}|/trigger`);
        } else {
            console.error('triggerSlash 函数不可用');
        }
    }
    document.getElementById('voiceModal').style.display = 'none';
    document.getElementById('voiceInput').value = '';
}

// 修改第一个控制按钮的onclick事件
document.querySelector('.call-controls button:first-child').onclick = showVoiceModal;
</script>
</body>
</html>
