<script>
// 点击特效
document.addEventListener('click', (e) => {
    const heart = document.createElement('div');
    heart.className = 'heart';
    heart.innerHTML = '♥';
    heart.style.left = e.clientX + 'px';
    heart.style.top = e.clientY + 'px';
    document.body.appendChild(heart);

    setTimeout(() => {
        heart.remove();
    }, 3000);
});
// 将点击声音和特效添加到所有可点击元素
document.addEventListener('DOMContentLoaded', () => {
    // 创建音效对象
    const clickSound = new Audio('https://assets.mixkit.co/active_storage/sfx/2568/2568-preview.mp3');

    // 为所有可点击元素添加类名
    const clickableElements = [
        '.nav-profile',
        '.nav-settings',
        '.post-avatar',
        '.post-image',
        '.post-action',
        '.bottom-nav-item',
        '.post-content',
        '.comment',
        '.comment-avatar'
    ];

    // 为每个可点击元素添加可点击类和事件监听器
    clickableElements.forEach(selector => {
        document.querySelectorAll(selector).forEach(element => {
            element.classList.add('clickable');

            element.addEventListener('click', () => {
                // 播放音效
                clickSound.currentTime = 0;
                clickSound.play();

                // 创建点击特效
                createClickEffect(element);

                // 显示可爱的提示
                let message = '';
                if(element.classList.contains('post-action')) {
                    if(element.textContent.includes('♥')) {
                        message = 'いいね！💕';
                    } else if(element.textContent.includes('↺')) {
                        message = 'シェアしました！✨';
                    } else {
                        message = 'ブックマーク！🎀';
                    }
                } else if(element.classList.contains('nav-settings')) {
                    message = '設定を開く〜⚙️';
                } else if(element.classList.contains('post-image')) {
                    message = '写真を拡大！📷';
                } else if(element.classList.contains('post-avatar') || element.classList.contains('comment-avatar')) {
                    message = 'プロフィールへ💫';
                } else {
                    message = 'Click! ✧˖°';
                }
                showKawaiiToast(message);
            });
        });
    });

    // 创建点击特效
    function createClickEffect(element) {
        // 生成闪光效果
        const sparkleCount = 5;
        const rect = element.getBoundingClientRect();

        for(let i = 0; i < sparkleCount; i++) {
            const sparkle = document.createElement('div');
            sparkle.className = 'sparkle';

            // 随机位置
            sparkle.style.left = rect.left + Math.random() * rect.width + 'px';
            sparkle.style.top = rect.top + Math.random() * rect.height + 'px';

            document.body.appendChild(sparkle);

            // 移除特效
            setTimeout(() => sparkle.remove(), 1500);
        }

        // 添加缩放动画
        element.style.transform = 'scale(0.95)';
        setTimeout(() => {
            element.style.transform = '';
        }, 100);
    }
});

// 创建闪光效果
function createSparkles(element) {
    const rect = element.getBoundingClientRect();
    for (let i = 0; i < 5; i++) {
        const sparkle = document.createElement('div');
        sparkle.className = 'sparkle';
        sparkle.style.left = `${rect.left + Math.random() * rect.width}px`;
        sparkle.style.top = `${rect.top + Math.random() * rect.height}px`;
        document.body.appendChild(sparkle);

        setTimeout(() => sparkle.remove(), 1500);
    }
}

// 显示可爱的Toast提示
function showKawaiiToast(message) {
    const toast = document.createElement('div');
    toast.className = 'kawaii-toast';
    toast.textContent = message;
    document.body.appendChild(toast);

    // 触发重排以启动动画
    toast.offsetHeight;
    toast.style.opacity = '1';

    setTimeout(() => {
        toast.style.opacity = '0';
        setTimeout(() => toast.remove(), 300);
    }, 2000);
}

// 处理底部导航项的切换
document.querySelectorAll('.bottom-nav-item').forEach(item => {
    item.addEventListener('click', () => {
        document.querySelectorAll('.bottom-nav-item').forEach(navItem => {
            navItem.classList.remove('active');
        });
        item.classList.add('active');
    });
});

// 添加可爱的加载动画
function showLoading() {
    const loading = document.createElement('div');
    loading.className = 'loading-animation';
    document.body.appendChild(loading);
    loading.style.display = 'block';

    return loading;
}

function hideLoading(loading) {
    loading.style.display = 'none';
    loading.remove();
}

// 模拟页面加载
window.addEventListener('load', () => {
    const loading = showLoading();
    setTimeout(() => hideLoading(loading), 1000);
});
// 将这段代码添加到script标签中
document.querySelectorAll('.post-image').forEach(image => {
    let descriptionElement = null;

    image.addEventListener('click', () => {
        if (descriptionElement) {
            // 如果描述已存在,则移除
            descriptionElement.remove();
            descriptionElement = null;
        } else {
            // 创建新的描述元素
            descriptionElement = document.createElement('div');
            descriptionElement.className = 'image-description';
            descriptionElement.style.cssText = `
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
                background: rgba(255, 255, 255, 0.9);
                padding: 20px;
                color: #ff69b4;
                font-size: 14px;
                line-height: 1.6;
                white-space: pre-wrap;
                display: flex;
                align-items: center;
                justify-content: center;
                text-align: center;
                animation: fadeIn 0.3s ease;
            `;
            descriptionElement.textContent = image.dataset.description;
            image.style.position = 'relative';
            image.appendChild(descriptionElement);
        }
    });
});
// 为底部导航项添加点击发送消息功能
document.querySelectorAll('.bottom-nav-item').forEach(item => {
    item.addEventListener('click', () => {
        // 移除其他项的active类
        document.querySelectorAll('.bottom-nav-item').forEach(navItem => {
            navItem.classList.remove('active');
        });

        item.classList.add('active');

        // 获取导航项的文本内容
        const navText = item.querySelector('.bottom-nav-text').textContent;

        // 发送消息到聊天系统
        if (typeof triggerSlash === 'function') {
            triggerSlash(`/send 查看${navText}|/trigger`);
        } else {
            console.error('triggerSlash 函数不可用。');
        }
    });
});
// 为设置图标添加点击发送消息功能
document.querySelector('.nav-settings').addEventListener('click', () => {
    if (typeof triggerSlash === 'function') {
        triggerSlash(`/send 查看设置|/trigger`);
    } else {
        console.error('triggerSlash 函数不可用。');
    }
});
// 为动态内容和评论区的用户名添加点击发送消息功能
function addProfileClickHandlers() {
    // 为动态内容的用户名添加点击事件
    document.querySelectorAll('.post-name').forEach(nameElement => {
        nameElement.style.cursor = 'pointer';  // 添加鼠标指针样式
        nameElement.addEventListener('click', (e) => {
            e.stopPropagation();  // 阻止事件冒泡
            const name = nameElement.textContent.trim();
            if (typeof triggerSlash === 'function') {
                triggerSlash(`/send 查看${name}的动态主页|/trigger`);
            } else {
                console.error('triggerSlash 函数不可用。');
            }
        });
    });

    // 为评论区的用户名添加点击事件
    document.querySelectorAll('.comment-name').forEach(nameElement => {
        nameElement.style.cursor = 'pointer';  // 添加鼠标指针样式
        nameElement.addEventListener('click', (e) => {
            e.stopPropagation();  // 阻止事件冒泡
            const name = nameElement.textContent.trim();
            if (typeof triggerSlash === 'function') {
                triggerSlash(`/send 查看${name}的动态主页|/trigger`);
            } else {
                console.error('triggerSlash 函数不可用。');
            }
        });
    });
}

// 在页面加载完成后调用添加点击处理函数
document.addEventListener('DOMContentLoaded', addProfileClickHandlers);
</script>
