<script>
document.addEventListener('DOMContentLoaded', () => {
    const clickSound = new Audio('https://assets.mixkit.co/active_storage/sfx/2568/2568-preview.mp3');

    document.querySelector('.profile-section').addEventListener('click', (e) => {
        if (!e.target.closest('.back-button') && !e.target.closest('.profile-action-btn')) {
            clickSound.currentTime = 0;
            clickSound.play();
            createClickEffect(e.target);

            const name = document.querySelector('.profile-name').textContent.trim();
            if (typeof triggerSlash === 'function') {
                triggerSlash(`/send 查看${name}发来的消息|/trigger`);
            }
            showKawaiiToast(`与${name}开始私聊～💕`);
        }
    });

    document.querySelector('.back-button').addEventListener('click', () => {
        clickSound.currentTime = 0;
        clickSound.play();
        createClickEffect(document.querySelector('.back-button'));
        showKawaiiToast('前のページへ戻ります〜💕');

        if (typeof triggerSlash === 'function') {
            triggerSlash('/send 返回动态|/trigger');
        }
    });

    document.querySelectorAll('.profile-action-btn').forEach(btn => {
        btn.addEventListener('click', (e) => {
            e.stopPropagation();
            clickSound.currentTime = 0;
            clickSound.play();
            createClickEffect(btn);
            showKawaiiToast(`${btn.textContent}します〜✨`);
        });
    });

    document.querySelectorAll('.comment-name').forEach(nameElement => {
        nameElement.style.cursor = 'pointer';
        nameElement.addEventListener('click', (e) => {
            e.stopPropagation();
            clickSound.currentTime = 0;
            clickSound.play();
                        createClickEffect(nameElement);

            const name = nameElement.textContent.trim();
            if (typeof triggerSlash === 'function') {
                triggerSlash(`/send 查看${name}的动态主页|/trigger`);
            }
            showKawaiiToast(`偷窥一下${name}～✨`);
        });
    });

    document.querySelectorAll('.bottom-nav-item').forEach(item => {
        item.addEventListener('click', () => {
            clickSound.currentTime = 0;
            clickSound.play();
            createClickEffect(item);

            document.querySelectorAll('.bottom-nav-item').forEach(navItem => {
                navItem.classList.remove('active');
            });

            item.classList.add('active');
            const navText = item.querySelector('.bottom-nav-text').textContent;
            showKawaiiToast(`${navText}へ移動〜✨`);

        });
    });

    document.querySelectorAll('.stat-item').forEach(item => {
        item.addEventListener('click', () => {
            clickSound.currentTime = 0;
            clickSound.play();
            createClickEffect(item);
            const label = item.querySelector('span:last-child').textContent;
            showKawaiiToast(`${label}を確認中～✨`);
        });
    });

    document.querySelectorAll('.tweet-image').forEach(image => {
        let descriptionElement = null;

        image.addEventListener('click', () => {
            clickSound.currentTime = 0;
            clickSound.play();
            createClickEffect(image);

            if (descriptionElement) {
                descriptionElement.remove();
                descriptionElement = null;
            } else {
                descriptionElement = document.createElement('div');
                descriptionElement.className = 'image-description';
                descriptionElement.textContent = image.dataset.description;
                image.appendChild(descriptionElement);
                showKawaiiToast('写真の詳細を表示✨');
            }
        });
    });

    document.querySelectorAll('.tweet-action').forEach(action => {
        action.addEventListener('click', () => {
            clickSound.currentTime = 0;
            clickSound.play();
            createClickEffect(action);

            let message = '';
            if (action.textContent.includes('♥')) {
                message = 'いいね！💕';
            } else if (action.textContent.includes('↺')) {
                message = 'シェアしました！✨';
            } else {
                message = 'ブックマーク！🎀';
            }
            showKawaiiToast(message);
        });
    });

    document.querySelectorAll('.comment').forEach(comment => {
        comment.addEventListener('click', (e) => {
            if (!e.target.classList.contains('comment-name')) {
                clickSound.currentTime = 0;
                clickSound.play();
                createClickEffect(comment);
                showKawaiiToast('コメントありがとう！💖');
            }
        });
    });

    function createClickEffect(element) {
        const sparkleCount = 5;
        const rect = element.getBoundingClientRect();

        for(let i = 0; i < sparkleCount; i++) {
            const sparkle = document.createElement('div');
            sparkle.className = 'sparkle';
            sparkle.style.left = rect.left + Math.random() * rect.width + 'px';
            sparkle.style.top = rect.top + Math.random() * rect.height + 'px';
            document.body.appendChild(sparkle);
            setTimeout(() => sparkle.remove(), 1500);
        }
    }

    function showKawaiiToast(message) {
        const toast = document.createElement('div');
        toast.className = 'kawaii-toast';
        toast.textContent = message;
        document.body.appendChild(toast);

        toast.offsetHeight;
        toast.classList.add('show');

        setTimeout(() => {
            toast.classList.remove('show');
            setTimeout(() => toast.remove(), 300);
        }, 2000);
    }

    // 实现平滑滚动
    const appContainer = document.querySelector('.app-container');
    let lastScrollTop = 0;

    appContainer.addEventListener('scroll', () => {
    const scrollTop = appContainer.scrollTop;
    const profileSection = document.querySelector('.profile-section');
    const profileInfo = document.querySelector('.profile-info');
    const timeline = document.querySelector('.timeline');

    if (scrollTop > lastScrollTop) {
        profileSection.style.transform = `translateY(-${Math.min(scrollTop, 200)}px)`;
        profileSection.style.opacity = Math.max(0, 1 - scrollTop / 200);
        profileInfo.style.transform = `translateY(-${Math.min(scrollTop, 200)}px)`; // 添加个人信息栏的滚动
        timeline.style.marginTop = `-${Math.min(scrollTop, 200)}px`;
    } else {
        profileSection.style.transform = `translateY(-${Math.min(scrollTop, 200)}px)`;
        profileSection.style.opacity = Math.max(0, 1 - scrollTop / 200);
        profileInfo.style.transform = `translateY(-${Math.min(scrollTop, 200)}px)`; // 添加个人信息栏的滚动
        timeline.style.marginTop = `-${Math.min(scrollTop, 200)}px`;
    }

    lastScrollTop = scrollTop;
});

    // 添加下拉刷新效果
    let touchStartY = 0;
    let refreshing = false;

    appContainer.addEventListener('touchstart', (e) => {
        touchStartY = e.touches[0].clientY;
    });

    appContainer.addEventListener('touchmove', (e) => {
        if (refreshing) return;

        const touchY = e.touches[0].clientY;
        const scrollTop = appContainer.scrollTop;

        if (scrollTop <= 0 && touchY - touchStartY > 50) {
            refreshing = true;
            showKawaiiToast('更新中...✨');

            setTimeout(() => {
                refreshing = false;
                showKawaiiToast('更新完了！💕');
            }, 1000);
        }
    });
});
</script>
</body>
</html>
