.dog-ear.left {
  left: 5px;
  transform: rotate(-30deg);
  animation: earWiggleLeft 2s ease-in-out infinite;
}

.dog-ear.right {
  right: 5px;
  transform: rotate(30deg);
  animation: earWiggleRight 2s ease-in-out infinite;
}

@keyframes dogBounce {
  0%, 100% {
    transform: translateX(-50%) translateY(0);
