  50% {
    transform: translateX(-50%) translateY(-20px);
  }
}

@keyframes earWiggleLeft {
  0%, 100% {
    transform: rotate(-30deg);
  }
  50% {
    transform: rotate(-40deg);
  }
}

@keyframes earWiggleRight {
  0%, 100% {
