<script>
// 初始化漂浮狗爪
function initFloatingPaws() {
  const container = document.getElementById('floatingPaws');
  const pawCount = 15;

  for (let i = 0; i < pawCount; i++) {
    const paw = document.createElement('span');
    paw.className = 'paw';
    paw.textContent = '🐾';
    paw.style.left = Math.random() * 100 + '%';
    paw.style.animationDelay = Math.random() * 15 + 's';
    paw.style.animationDuration = (15 + Math.random() * 10) + 's';
    container.appendChild(paw);
  }
}

// Tips面板控制
let tipsExpanded = false;

function toggleTips() {
  const banner = document.getElementById('tipsBanner');
  tipsExpanded = !tipsExpanded;

  if (tipsExpanded) {
    banner.classList.add('expanded');
  } else {
    banner.classList.remove('expanded');
  }
}

// 键盘快捷键
document.addEventListener('keydown', (e) => {
  // 空格键切换Tips
  if (e.code === 'Space' && e.target.tagName !== 'INPUT') {
    e.preventDefault();
    toggleTips();
  }

  // ESC键关闭Tips
  if (e.key === 'Escape' && tipsExpanded) {
    toggleTips();
  }

  // 数字键快速选择
  const num = parseInt(e.key);
  if (num >= 1 && num <= 9) {
    const button = document.querySelector(`[data-swipe="${num}"] .enter-button`);
    if (button) {
      button.click();
    }
  }
});

// 鼠标位置追踪（用于卡片光效）
document.addEventListener('mousemove', (e) => {
  const cards = document.querySelectorAll('.narrative-card');

  cards.forEach(card => {
    const rect = card.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * 100;
    const y = ((e.clientY - rect.top) / rect.height) * 100;

    card.style.setProperty('--mouse-x', `${x}%`);
    card.style.setProperty('--mouse-y', `${y}%`);
  });
});

// 加载开场白数据
async function loadNarratives() {
  const grid = document.getElementById('narrativesGrid');
  const loader = document.getElementById('loadingContainer');
  const countElement = document.getElementById('narrativeCount');

  try {
    // 获取聊天消息
    const messages = await getChatMessages("0", { include_swipe: true });

    if (!messages || messages.length === 0 || !messages[0].swipes || messages[0].swipes.length <= 1) {
      grid.innerHTML = `
        <div class="error-container">
          <div class="error-dog">🐕‍🦺</div>
          <div class="error-text">汪呜~ 还没有发现任何开场白</div>
        </div>
      `;
      countElement.textContent = '0 个开场白';
      loader.classList.add('hidden');
      return;
    }

    const swipeCount = messages[0].swipes.length - 1;
    countElement.textContent = `${swipeCount} 个开场白`;

    // 延迟隐藏加载器
    setTimeout(() => {
      loader.classList.add('hidden');
    }, 1000);

    // 创建卡片
    for (let i = 1; i < messages[0].swipes.length; i++) {
      const content = messages[0].swipes[i];
      const swipeId = i;

      // 提取元数据
      let title = `开场白 ${i}`;
      let description = "";

      const titleMatch = content.match(/<!--\s*title:\s*(.*?)\s*-->/);
      if (titleMatch) title = titleMatch[1].trim();

      const descMatch = content.match(/<!--\s*desc:\s*(.*?)(?=\s*-->)-->/s);
      if (descMatch) {
        description = descMatch[1].trim();
      } else {
        // 提取纯文本内容作为描述
        const cleanContent = content
          .replace(/<!--.*?-->/gs, '')
          .replace(/<[^>]*>/g, '')
          .trim();
        description = cleanContent || "神秘的开场白，等待你的探索...";
      }

      // 创建卡片元素
      const card = document.createElement('div');
      card.className = 'narrative-card';
      card.style.setProperty('--card-index', i - 1);
      card.setAttribute('data-swipe', swipeId);

      card.innerHTML = `
        <div class="card-header">
          <div class="card-badge">${String(i).padStart(2, '0')}</div>
          <div class="card-title-group">
            <h3 class="card-title">${title}</h3>
            <p class="card-subtitle">NARRATIVE #${i}</p>
          </div>
        </div>

        <div class="card-body">
          <div class="card-description">
            ${description}
          </div>
        </div>

        <div class="card-footer">
          <button class="enter-button">
            <span class="button-content">
              <span>进入此开场白</span>
              <span class="button-icon">🐾</span>
            </span>
          </button>
        </div>
      `;

      grid.appendChild(card);

      // 绑定点击事件
      const enterButton = card.querySelector('.enter-button');
      enterButton.addEventListener('click', async (e) => {
        e.stopPropagation();
        await switchToNarrative(swipeId);
      });

      // 整卡片可点击
      card.addEventListener('click', (e) => {
        if (!e.target.closest('.enter-button')) {
          enterButton.click();
        }
      });
    }

  } catch (error) {
    console.error('加载开场白失败:', error);
    grid.innerHTML = `
      <div class="error-container">
        <div class="error-dog">😵</div>
        <div class="error-text">汪！加载失败了，请刷新重试</div>
      </div>
    `;
    countElement.textContent = '加载失败';
    loader.classList.add('hidden');
  }
}

// 切换到指定开场白
async function switchToNarrative(swipeId) {
  // 创建华丽的过渡效果
  const transition = document.createElement('div');
  transition.style.cssText = `
    position: fixed;
    inset: 0;
    background: radial-gradient(circle at center, var(--golden) 0%, var(--midnight) 50%);
    z-index: 10000;
    opacity: 0;
    transition: opacity 0.8s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    gap: 20px;
  `;

  const transitionContent = document.createElement('div');
  transitionContent.innerHTML = `
    <div style="font-size: 5rem; animation: spin 1s linear infinite;">🌟</div>
    <div style="font-size: 1.5rem; color: var(--midnight); font-weight: 600;">正在进入方亦楷的世界...</div>
  `;
  transition.appendChild(transitionContent);

  document.body.appendChild(transition);

  // 添加旋转动画
  const style = document.createElement('style');
  style.textContent = `
    @keyframes spin {
      from { transform: rotate(0deg); }
      to { transform: rotate(360deg); }
    }
  `;
  document.head.appendChild(style);

  // 触发过渡
  requestAnimationFrame(() => {
    transition.style.opacity = '1';
  });

  try {
    const messages = await getChatMessages("0", { include_swipe: true });
    if (messages && messages[0].swipes && messages[0].swipes[swipeId]) {
      await setChatMessage(messages[0].swipes[swipeId], 0, {
        swipe_id: swipeId,
        refresh: 'display_and_render_current'
      });
    }
  } catch (error) {
    console.error('切换开场白失败:', error);
    transition.remove();
    style.remove();
  }
}

// 初始化
window.addEventListener('load', () => {
  initFloatingPaws();
  setTimeout(loadNarratives, 500);
});

// 添加平滑滚动
document.documentElement.style.scrollBehavior = 'smooth';

// 性能优化：页面不可见时暂停动画
document.addEventListener('visibilitychange', () => {
  const animations = document.getAnimations();
  if (document.hidden) {
    animations.forEach(anim => anim.pause());
  } else {
    animations.forEach(anim => anim.play());
  }
});

// 添加音效（可选）
const audioContext = new (window.AudioContext || window.webkitAudioContext)();

function playHoverSound() {
  const oscillator = audioContext.createOscillator();
  const gainNode = audioContext.createGain();

  oscillator.connect(gainNode);
  gainNode.connect(audioContext.destination);

  oscillator.frequency.value = 600;
  oscillator.type = 'sine';

  gainNode.gain.setValueAtTime(0.05, audioContext.currentTime);
  gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.1);

  oscillator.start(audioContext.currentTime);
  oscillator.stop(audioContext.currentTime + 0.1);
}

// 为交互元素添加音效
document.addEventListener('mouseenter', (e) => {
  if (e.target.matches('.enter-button, .narrative-card, .tips-toggle-btn')) {
    try {
      playHoverSound();
    } catch (err) {
      // 忽略音频错误
    }
  }
}, true);
</script>
</body>
</html>
